const App = () => {
  const course = "Half stack application development";
  const part1 = "Fundamentals of React";
  const exercises1 = 10;
  const part2 = "Using props to pass data";
  const exercises2 = 7;
  const part3 = 'State of a component';
  const exercises3 = 14;


  return (
    <div>
      <Header />
      <Content />
     <total />
    </div>
  );

  function Header() {
    return <h1>{course}</h1>;
  }

  function Content () {
    return (
      <>
        <p>
          {part1} {exercises1}
        </p>
        <p>
          {part2} {exercises2}
        </p>
        <p>
          {part3} {exercises3}
        </p>
    </>
    )

    function Total () {
      return (
        <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
      )
    }


  }
}

export default App;
