import { createStore } from 'redux'

const notificationReducer = (state = "testing", action) => {
    switch(action.type) {
        case 'USER_ERROR':
            return 'You entered the wrong username or password'
        case 'RESET':
            return state
        case 'BLOG_ADDED': 
            return 'You added a new blog'
        default: 
            return state
    }
}

const notificationStore = createStore(notificationReducer)

export default notificationStore
