import { createStore } from 'redux'
import './notificationStore.css'

const notificationReducer = (state = <div></div>, action) => {
    switch(action.type) {
        case 'USER_ERROR':
            return (
                <div className="userError">
                    You entered the wrong username or password.
                </div>)
        case 'RESET':
            return state
        case 'BLOG_ADDED': 
            return (
                <div className="addBlog">
                    You added a new blog.
                </div>
            )
        default: 
            return action.type = 'USER_ERROR'
    }
}

const notificationStore = createStore(notificationReducer)

export default notificationStore
