import { createStore } from 'redux'

const notificationReducer = (state = <div>testing</div>, action) => {
    switch(action.type) {
        case 'USER_ERROR':
            return (
                <div>
                    You entered the wrong username or password
                </div>)
        case 'RESET':
            return state
        case 'BLOG_ADDED': 
            return 'You added a new blog'
        default: 
            return state
    }
}

const notificationStore = createStore(notificationReducer)

export default notificationStore
